mapper.select:
select t4.* from ( select t3.* ,floor((rownum - 1) / #{pageSize} + 1 ) as page from ( select t1.order_seq ,t1.loss_code ,t1.loss_desc ,TRANSLATE(t1.set_name, '0123456789'||t1.set_name, '0123456789') as set_name_num ,t1.set_name ,t1.paint_count || 'íšŒ' as paint_count ,t1.pre_loss ,t1.post_loss ,round(t1.pre_loss_rate) as pre_loss_rate ,round(t1.post_loss_rate) as post_loss_rate ,(select sd_desc as text from STX_DIS_SD_CODE where sd_type='PAINT_TYPE' and sd_code = t1.paint_type) as paint_type ,t1.stage_desc ,nvl(t1.remark,'') as remark from stx_dis_pis_loss t1 where 1=1
and t1.loss_code like #{lossCode} || '%'
order by t1.order_seq, t1.loss_code, t1.loss_desc, cast(set_name_num as int), t1.paint_count ) t3 ) t4 where page = #{curPageNo}
